# input_file: It's the file given to us

def read_text(file_name):
  res = []
  with open(file_name, 'r', encoding="utf-8") as dfile:
    for line in dfile.readlines():
      res.append(line.strip())
  return res

def store_text(res, file_name):
  with open(file_name, 'w', encoding='utf-8') as dfile:
    for z in res:
      dfile.write("%s\t%s" % (res["input_name"], ", ".join(res["matching_names"])))

