import fuzzy

dmetaphone = fuzzy.DMetaphone()
soundex = fuzzy.Soundex(4)

from collections import defaultdict


def create_phonetics(name_list):
  res = defaultdict(lambda: defaultdict(lambda: []))
  for z in name_list:
    #res['soundex'][soundex(z)].append(z)
    res['nysiis'][fuzzy.nysiis(z)].append(z.lower())
    res['dmeta'][str(dmetaphone(z)[0])].append(z.lower())
  return res

